#!/bin/bash\n# Sync uDESK documentation between local files and Notion\n\nPROJECT_ROOT=\"$(cd \"$(dirname \"${BASH_SOURCE[0]}\")\" && pwd)\"\nNOTION_PAGE_ID=\"260954f7-ff4e-81f5-a37c-d76191498be2\"\n\necho \"=== uDESK Documentation Sync ===\"\necho \"Syncing between local docs and Notion workspace...\"\necho \"\"\n\n# Function to update Notion page with local markdown content\nsync_to_notion() {\n    local file=$1\n    local title=$2\n    \n    if [ -f \"$file\" ]; then\n        echo \"Syncing $file to Notion...\"\n        # In practice, you'd use Claude's Notion API integration here\n        # For now, we'll create instructions\n        echo \"  • File: $file\"\n        echo \"  • Title: $title\"\n        echo \"  • Content: $(wc -l < \"$file\") lines\"\n    fi\n}\n\n# Sync main documentation files\necho \"Documentation files to sync:\"\nsync_to_notion \"$PROJECT_ROOT/README.md\" \"Project Overview\"\nsync_to_notion \"$PROJECT_ROOT/QUICKSTART.md\" \"Quick Start Guide\"\nsync_to_notion \"$PROJECT_ROOT/docs/INSTALL.md\" \"Installation Guide\"\nsync_to_notion \"$PROJECT_ROOT/docs/ROLES.md\" \"Roles & Permissions\"\nsync_to_notion \"$PROJECT_ROOT/docs/BUILD.md\" \"Build Guide\"\n\necho \"\"\necho \"Build status:\"\nif [ -d \"$PROJECT_ROOT/build\" ]; then\n    echo \"  • Built packages: $(ls $PROJECT_ROOT/build/*.tcz 2>/dev/null | wc -l)\"\nelse\n    echo \"  • No packages built yet\"\nfi\n\nif [ -d \"$PROJECT_ROOT/out\" ]; then\n    echo \"  • Built images: $(ls $PROJECT_ROOT/out/*.iso 2>/dev/null | wc -l)\"\nelse\n    echo \"  • No images built yet\"\nfi\n\necho \"\"\necho \"✓ Documentation sync complete\"\necho \"Notion workspace: https://www.notion.so/$NOTION_PAGE_ID\"\n